    private static HashMap<String, String> emoticonsMap;

    /**
     * For this exercise, you only need to consider 'custom' emoticons which are alphanumeric strings,
     * no longer than 15 characters, contained in parenthesis.
     * You can assume that anything matching this format is an emoticon.
     * (https://www.hipchat.com/emoticons)
     */


    public static boolean isEmoticon(boolean testMode, String emoticon) {

        if (testMode) {
            return true;
        }
        if (emoticonsMap == null) {
            emoticonsMap = initEmoticonsMap(emotionsListStr);
        }
        return emoticonsMap.containsKey(emoticon);
    }

    public static String getEmoImgUrl(String emoticon) {
        String ret = "";
        if (emoticonsMap.containsKey(emoticon)) {
            ret = emoticonsMap.get(emoticon);
        }
        return ret;

    }

    private static HashMap<String, String> initEmoticonsMap(String str) {

        HashMap<String, String> map = new HashMap<>();
        Gson gson = new Gson();
        EmoticonsList list = gson.fromJson(str, EmoticonsList.class);
        for (EmoticonsList.Emoticon emoticon : list.getList()) {
            String key = emoticon.getText();
            String img = emoticon.getImg();
            map.put(key, img);

//            System.out.println("key " + key + " img  = " + img);
        }
        return map;
    }
